<script>
  window.addEventListener('DOMContentLoaded', function() {
    fetch('/_pages/about.md') // Replace 'about.md' with the actual name of your .md file
      .then(response => response.text())
      .then(markdownContent => {
        const htmlContent = marked(markdownContent);
        document.getElementById('about-content').innerHTML = htmlContent;
      })
      .catch(error => {
        console.error('Error fetching or converting Markdown file:', error);
      })
  });
</script>